#define PI 3.14159265359

vec3 palette(float t){
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) * cos(2.*PI*(vec3(1.0, 1.0, 1.0)*t+vec3(0.00, 0.33, 0.66)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);
    for(float i=1.; i < 3.; i++){
        vec2 uv0 = fract(uv * i);
        uv0 = uv0*2. - 1.;
        uv0 *= 1.4;
        float d = length(uv0);
        d = sin(10.*d - iTime)*.5 + .5;
        d = pow(0.2/d, i * .25);
        d -= 0.4;
        col += d*smoothstep(0.01, 0.1, 1.-length(uv/(1.)))*palette(length(uv)+0.03*iTime);
    }

    float d = length(uv);
    d = sin(10.*d - iTime)*.5 + .5;
    d = pow(0.05/d, .25);
    d -= 0.3;
    col += d*(1.-length(uv));
    col *= (0.3/length(uv));

    float r = length(uv);
    r = 20.*sin(5.*(r + PI/10. - .98))-19.99;
    r = 0.1/r;
    r = clamp(r, 0., 1.);
    col += r;


    fragColor.rgb = col;
}
