const float dt = .1;
const float len = 16.;
const float zoom = 4.;

vec2 diff(float t){
    return vec2(sin(t*t),cos(t*t));
}

float clothoid_distance(vec2 uv){
    float dis = 1e38;

    vec2 p = vec2(0);
    for(float t=0.;t<len;t+=dt){
        vec2 tang = diff(t+dt/2.);
        float tang2 = dot(tang,tang);

        float dt2 = clamp(dot(tang,uv-p)/tang2,0.,dt);
        vec2 p2 = p + dt2 * diff(t+dt2/2.);

        float dt3 = clamp(dot(tang,-uv-p)/tang2,0.,dt);
        vec2 p3 = p + dt3 * diff(t+dt3/2.);

        dis = min(dis,distance(uv,p2));
        dis = min(dis,distance(-uv,p3));
        p += dt * tang;
    }

    return dis;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
	uv -= .5;
    uv.y *= iResolution.y/iResolution.x;

    uv *= zoom;

    float dis = clothoid_distance(uv);

    dis /= zoom;

    vec3 color = vec3(1.0) - sign(dis)*vec3(0.1,0.4,0.7);
	color *= 1.0 - exp(-8.0*abs(dis));
	color *= 0.8 + 0.2*cos(480.0*dis);
	color = mix(color, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(dis)));

    fragColor = vec4(color,1);
}
