void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 normal = vec3(0.0,0.0,1.0);


    float delta = 2.;
    vec2 uvc = 2.0*(uv + vec2(iTime*0.1,0.0));
    vec3 albedo = texture(iChannel0,uvc).rgb;
    float p0 = length(albedo);
    float p0dx = length(texture(iChannel0,uvc+vec2(1.0/iChannelResolution[0].x,0.0)).rgb);
    float p0dy = length(texture(iChannel0,uvc+vec2(0.0,1.0/iChannelResolution[0].y)).rgb);
    normal = normalize(cross(vec3(delta,0.0,p0dx-p0),vec3(0.0,delta,p0dy-p0)));



    vec3 cam_pos =  vec3( 0.5,0.5,2.0);
    vec3 light_pos = vec3(iMouse.xy/iResolution.xy*2.0-0.5,2.0);

    vec3 view_dir = normalize(vec3(uv,0.0)-cam_pos);
    vec3 light_dir = normalize(vec3(uv,0.0)-light_pos);

    vec3 ref = -reflect(view_dir,normal);


    vec3 col =albedo*0.2 + (albedo*0.3+0.5)* pow(max(0.0,dot(light_dir,ref)),30.0);



    fragColor = vec4(col,1.0);
}
