void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 3.0;
    uv.x *= (iResolution.x / iResolution.y);

    float sa = sin(iTime);
    float ca = cos(iTime);

    float a = 0.1 * (sin(iTime * 3.1234) + sin(iTime * 2.9382) + 1.0);

    uv = vec2(uv.x * ca - uv.y * sa, uv.y * ca + uv.y * sa);

    uv += vec2(a * sin(uv.y * 5.0 + iTime * 3.1), a * cos(uv.x * 5.0 + iTime * 2.7));

    vec2 stepUV = step(fract(uv), vec2(0.5));
    float c = float(stepUV.x != stepUV.y);
    fragColor = vec4(c, c, c, 1.0);
}
