void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float temp = .3;

    vec3 c = texture(iChannel0, uv).rgb;
    vec3 c1 = vec3(0.678,0.937,1.000);
    vec3 c2 = vec3(0.000,0.000,0.000);

    float d1 = dot(c, c1 * temp) * 2.0 - 1.0;
    float d2 = dot(c, c2 * temp) * 2.0 - 1.0;

    float p = d1 / (d1 + d2);

    c = mix(c1, c2, p);

    // Output to screen
    fragColor = vec4(c,1.0);
}
