float circle(vec2 uv, vec2 center, float r, float k)
{
    return smoothstep(r, r + k, length(uv + center));
}

float grid(vec2 uv, float size, float effect)
{
    uv = fract(uv * size) - 0.5;
    return circle(uv, vec2(0.0), 0.2 * effect, 0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;

    vec2 mouse = -(iMouse.xy - iResolution.xy / 2.0) / iResolution.y;    
    float effect = circle(uv, mouse, 0.0, 1.0) + 0.7;
    fragColor = vec4(grid(uv * effect, 30.0, 1.0 - effect * 0.5));
}
