void mainImage( out vec4 Q, in vec2 U )
{ R = iResolution.xy;
     U = U.yx*R.x/R.y/N;
     Q = vec4(0);
     for (float i = 0.; i < N*N; i++) {
         Q += .1*B(vec3(U,i));
     }
}

// 3D utilities 
#define N 9.
#define dt .5
vec2 R;
//  [ [0,R/N]; [0,N*N]  ] < -- > [0,R]
#define Main void mainImage (out vec4 Q, in vec2 _U) {R = iResolution.xy;vec3 U = d3(_U);
#define A(U) s3d(iChannel0,U)
#define B(U) s3d(iChannel1,U)
#define R3 vec3(R/N,N*N)
vec2 d2 (vec3 U) {
	return U.xy+vec2(mod(U.z,N),floor(U.z/N))*R/N;
}
vec3 d3 (vec2 u) {
    vec2 o = floor(u/R*N);
	return vec3(mod(u,R/N),o.x+o.y*N);
}
vec4 s3d (sampler2D T,vec3 U) {
    vec3 U0 = vec3 (U.xy,floor(U.z)),
         U1 = vec3 (U.xy, ceil(U.z));
    vec4 o = mix(
        texture(T,d2(U0)/R),
        texture(T,d2(U1)/R),
        fract(U.z)
    );
    return o;
}

Main 
    Q = A(U);
    vec4 px = A(U+vec3(1,0,0));
    vec4 nx = A(U-vec3(1,0,0));
    vec4 py = A(U+vec3(0,1,0));
    vec4 ny = A(U-vec3(0,1,0));
    vec4 pz = A(U+vec3(0,0,1));
    vec4 nz = A(U-vec3(0,0,1));
    Q.xyz -= .5*vec3(px.w-nx.w,py.w-ny.w,pz.w-nz.w);
    if (U.x<1.) Q.xyz *= 0.;
    if (U.z<1.) Q.xyz *= 0.;
}

Main 
    Q = A(U);
    vec4 px = A(U+vec3(1,0,0));
    vec4 nx = A(U-vec3(1,0,0));
    vec4 py = A(U+vec3(0,1,0));
    vec4 ny = A(U-vec3(0,1,0));
    vec4 pz = A(U+vec3(0,0,1));
    vec4 nz = A(U-vec3(0,0,1));
    Q.w -= .5*(px.x-nx.x+py.y-ny.y+pz.z-nz.z);
}

Main 

    if (iFrame % 4 == 0) {
        for (int i = 0; i < 4*3; i++)
            U -= dt*A(U).xyz;
    }
    Q = A(U);
    Q.x += 3e-3*B(U).w;
    if (length(U-vec3(.1,.5,.5)*R3)<.8*N) {
        Q.x = .5;
    }
}

Main 

    if (iFrame % 8 == 0) {
        for (int i = 0; i < 8*3; i++)
            U -= dt*A(U).xyz;
    }
    Q = B(U);
    if (U.x<1.||U.y<1.||U.z<1.) Q *= 0.;
    if (length(U-vec3(.1,.5,.5)*R3)<.8*N) Q = vec4(max(.5+.5*sin(iTime+vec3(1,2,3)),0.),1);
}
