#define T iTime * 0.8

vec2 N22(vec2 p)
{
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy)/iResolution.y;   
    vec3 col = vec3(uv, .0);
    float dist = .0;
    float minDist = 100.0;
    float cellIndex = 0.;

    if(false)
    {
        for(float i = 0.; i < 100.; i++)
        {
            vec2 n = N22(vec2(i));
            vec2 p = sin(n*T);
            dist = length(uv - p);
            if(dist < minDist)
            {
                minDist = dist;
                cellIndex = i;
            }
        }

        //col = vec3(minDist);
        col = (cellIndex / 100. + .1) * vec3(.2, .6, .8);
    }
    else
    {
        uv *= 30.;
        vec2 gv = fract(uv) - .5;
        vec2 id = floor(uv);
        vec2 cid = vec2(.0);
        float ed = .0;
        float md = .0;

        for(float y = -1.; y <= 1.; y++)
        {
            for(float x = -1.; x <= 1.; x++)
            {
                vec2 offset = vec2(x, y);
                vec2 n      = N22(offset + id);
                vec2 p      = sin(n * T)*.5 + offset;
                p           = gv - p;
                ed          = length(p);
                md          = abs(p.x) + abs(p.y);
                dist        = mix(ed, md, sin(iTime) * .5 + .5);
                if(dist < minDist)
                {
                    minDist = dist;
                    cid     = vec2(id + offset);
                }
            }
        }
        col = (smoothstep(.8, .0, minDist)) * vec3(.8, .2, .2);        
    }
    fragColor = vec4(col, 1.0);
}
