float ring(float len, float radius, float width) {
    return smoothstep(radius - width * 3.0, radius, len) - smoothstep(radius, radius + width * 2., len);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float progress = mod(iTime, 2.0) * 0.5;

    float progress1 = clamp(progress, 0.0, 0.8) / 0.8;
    float progress2 = clamp(progress - 0.2, 0.0, 1.0) / 0.8;

    vec2 center = 0.5 * iResolution.xy;
    // Normalized pixel coordinates (from 0 to 1)
    float len = length(fragCoord - center);


    vec3 ring1 = vec3(24.0, 256.0, 12.0);
    vec3 ring2 = vec3(24.0, 256.0, 12.0);

    float r1 = ring1.x + (ring1.y - ring1.x) * progress1;
    float r2 = ring2.x + (ring2.y - ring2.x) * progress2;

    float d1 = ring(len, r1, ring1.z);
    float d2 = ring(len, r2, ring2.z);

    float alpha1 = smoothstep(0.0, 0.25, progress1) - smoothstep(0.75, 1., progress1);

    float alpha2 = smoothstep(0.0, 0.25, progress2) - smoothstep(0.75, 1., progress2);

    float d = d1 * alpha1 + d2 * alpha2;


    vec2 offset = vec2(90., 90.) * vec2(d) * 0.3;

    fragColor = texture(iChannel0,(fragCoord + offset)/ iResolution.xy);
    fragColor = vec4(d);

}
