// https://www.shadertoy.com/view/X3SyD1
//
// https://www.shadertoy.com/view/M3jcD1
//
// https://www.shadertoy.com/view/XXjyzm
//
// Control the number of circles per layer when the mouse is operated horizontally.
// The mouse moves up and down to control the way it moves, and I did 4 ways.


#define TAU 6.2831853

float hash11(float p)
{// https://www.shadertoy.com/view/4djSRW  -  Dave_Hoskins.
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec4 hash41(float p)
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

vec4 drawMDL(vec2 u, vec2 n){
        n =normalize(n);
        vec4 z=vec4(0),w;


        u *= mat2(n.x,n.y,-n.y,n.x);
        u.x +=.25;
        u = (u-0.)*.65+.5;


        for(int i;i++<24;z=z.x*z+z.y*vec4(-z.y,z)-3.*(u.xyyy-.5))w=abs(z);
        w=clamp(w,0.,1.);
        return w;
}


void mainImage( out vec4 color, in vec2 cdnt )
{
        vec2 p = 5.*(cdnt * 2. - iResolution.xy) / iResolution.y;
        float N = 3. + floor(iMouse.x/iResolution.x*20.);
        int xyn=int(iMouse.y/iResolution.y*4.);
        if(length(iMouse.xy)<3.)N=9.;

        float r = 1.*sin(TAU / N / 2.), up = 1.+r, dw = 1.-r;
        float degree = up / dw;

        float t = iTime;
        if(xyn==1){
                 float app = atan(p.y, p.x);
                 float ixx=round((app / TAU + .5) * N);
                 ixx = float(int(ixx+N+N)%int(N));

                 t = 2.*(hash11(ixx)-.5)*t;
        }


        float zoom = pow(degree,  1.-fract(t));

        p *= zoom;


        float ap = atan(p.y, p.x), lp = length(p);



        //lp= length(p);

        float iy = floor( log(lp) / log(degree) );





        float rtx=0.;
        if(xyn==0) rtx=floor(4.*hash11(iy-floor(t))-2.)*t; // color 难以计算
        if(xyn==2) rtx=t;



        float ix = round((ap / TAU + .5) * N+rtx) ; 
        //ix = mod(ix, N);
        float ca = ((ix-rtx) / N - .5) * TAU;
        float dwy = pow(degree, iy), upy = dwy * degree;

        vec2 cent = (upy + dwy)*.5 * vec2(cos(ca), sin(ca));
        float R = (upy - dwy) * .5;
        float d = length(p - cent)-R;

        float ind = (iy - floor(t))*N + float(int(ix+1e-2+N*N*8.)%int(N));

        d /= zoom;

        color = vec4( exp(-60.* abs(d) ) ); 
        if(d<0. )color+=(.2+.8*hash41(ind))*(.5+.5*drawMDL((p-cent)/R, normalize(cent)  ));
}
