void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 colorData = texture(iChannel0, uv);
    if (colorData.x > 0.) {
        col = vec3(0.);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 c = vec4(0.);
    if (length(fragCoord - iMouse.xy) < 1. && iMouse.z > 0.) {
        c = vec4(1.);
    }
    int count = 
        (texelFetch(iChannel0, ivec2(fragCoord + vec2(1,1)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(1,0)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(1,-1)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(0,-1)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(-1,-1)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(-1,0)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(-1,1)), 0).x > 0. ? 1 : 0)
       + (texelFetch(iChannel0, ivec2(fragCoord + vec2(0,1)), 0).x > 0. ? 1 : 0)
    ;
    vec4 current = texelFetch(iChannel0, ivec2(fragCoord), 0);
    if (current.x > 0. && count > 1 && count < 4) {
        c = vec4(1., 0., 0., 1.);
    }
    if (current.x < .01 && count == 3) {
        c = vec4(1., 0., 0., 1.);
    }

    fragColor = c;
}
