void mainImage( out vec4 c, in vec2 U )
{
    c = texture(iChannel0, U/iResolution.xy);
}

#define smoothfactor .986
float sdf(vec2 uv) {
    float screen_border = length(uv-.5+vec2(sin(iTime*3.)*.5, cos(iTime*3.)*.5))*3.;
    float circle = distance(uv-.5, 
    vec2(
    sin(iTime*3.)*.2+cos(iTime*3.)*.1*sin(iTime*iTime), 
    cos(iTime*3.)*.2
    ))*8.;
    return min(screen_border, circle);
}
void mainImage(out vec4 c, vec2 U)
{
    U /= iResolution.xy;
    c = vec4(smoothstep(0., 15., 
        1.-sdf(U)
    )) * (
    vec4(sin(iTime * 2.), cos(iTime * 2.), 0,
    0) + 2.3)
        + texture(iChannel0, U) * smoothfactor;
}
