mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float easeInOutQuart(float x) {
return x < 0.5 ? 8. * x * x * x * x : 1. - pow(-2. * x + 2., 4.) / 2.;
}

// simple cellular
float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*
        43758.5453123);
}


// gradient color
vec3 palette( in float t )
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.8,0.8,1.0);
    vec3 c = vec3(1.000,1.000,1.000);
    vec3 d = vec3(0.000, 0.333, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv -= 0.5;
    //uv *= 8.0;
    uv *= 3.0 + easeInOutQuart(sin(iTime * 0.1) );
    uv -= vec2(0.5);
    float rotAngleSpeed = easeInOutQuart(sin(iTime * 0.05) );
    uv = rotate2d( rotAngleSpeed ) * uv;
    uv += vec2(0.5) + pow(iTime * 0.02,2.);

    float a =  clamp( texture (iChannel0, vec2 (0.5)).x,0.,1.) ;


    vec2 iuv = floor(uv) ;  
    vec2 fuv = fract(uv); 


    float iv = random( iuv  );
    float r = sin(iTime * iv * 2.0  );


    float d = length(fuv-.5) - clamp(sin(r * iv  * a),0.0,0.3 ) ; 
    vec3 col =  palette( iv + iTime * 0.5)  ;

    float glow = abs(0.15  / (d  ) ); 
    glow *= glow /10.0  * clamp(pow(r,8.),0.5,0.8) ;
    col *= glow ;
    col *= a>0. ? 1.+a : 1.;

    fragColor = vec4( vec3( col ),1.0);
}
