void mainImage( out vec4 color, in vec2 uv ){

float sk=6.;
if(mod(float(iFrame),sk)==0.){
   float a2=float(iFrame)/(200.*sk);
   vec4 col=0.95*texture(iChannel0,uv/iResolution.xy);
   for(float i=0.;i<=2.;i+=0.125){
   for(float theta=-500.; theta<=500.; theta+=0.25){

        float a=a2;
        float theta2=3.1415+0.+theta+1.*float(iFrame)/(1000.*sk);
        float r1 = 0.;



      r1=cos(a+floor(3.5*1.*theta))+i;
      r1=4.5*r1;
        float x1 = r1*cos(theta2);
        float y1 = r1*sin(theta2);
        vec2 vec = vec2(x1,y1);

        col+=(i+0.125)*0.05*pow(2.,-300.*length(1.*vec2(-2.25,-1.25)+2.5*uv/iResolution.y-vec))*vec4(0.1+i,0.5-0.2*i,0.8-i,0.);




    }
    }
    color=col;
    }else{
   color=texture(iChannel0,uv/iResolution.xy);
   }

}
