void mainImage( out vec4 c, in vec2 U )
{
    c = texture(iChannel0, U/iResolution.xy);
}

void mainImage(out vec4 c, vec2 U)
{
    U /= iResolution.xy;// antialiasing by FabriceNeyret2
    c = vec4(smoothstep(0., 15., 
        1. - length(U - iMouse.xy / iResolution.xy) * 3. // negative distance
    )) * (
    vec4(sin(iTime * 2.), cos(iTime * 2.), 0, // color
    0) + 1.)
        + texture(iChannel0, U) * 0.99;
}
