float rect(in vec2 uv, in vec2 xy1, in vec2 xy2) {
    vec2 d1 = step(xy1, uv);
    vec2 d2 = step(uv, xy2);
    return d1.x * d1.y * d2.x * d2.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 influenced_color = vec3(0.577, 0.441, 0.700);
    vec3 influencing_color_A = vec3(0.319, 0.167, 0.365);
    vec3 influencing_color_B = vec3(0.628, 0.526, 0.775);

    vec3 color = mix(influencing_color_A, influencing_color_B, step(.5, uv.x));

    float in_rect = 0.0;
    for (float i = 0.0; i <= 8.0; i++) {
        vec2 size = vec2(25, 25)/iResolution.xy;
        size = size - size/i;
        vec2 mid = vec2(tan(iTime/i)/2.0+0.5, sin(iTime*i)/2.0+0.5);
        in_rect += rect(uv, mid-size, mid+size);
    }

    color = mix(
        color,
        influenced_color,
        min(in_rect, 1.0));

    fragColor = vec4(color,1.0);
}
