// Shadertoy-koodi, jossa on enemmän liikettä, nopeutta ja morffautumista
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalisoidaan koordinaatit ja lisätään ajastin
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5; // Lisätään nopeutta

    // Sumu
    vec3 col = vec3(0.0);
    float noise = fract(sin(dot(uv.xy, vec2(12.9898,78.233))) * 43758.5453);
    col += 0.1 * vec3(noise, noise * 0.5, noise * 0.2);

    // Pyörteinen sumu
    float r = length(uv);
    float a = atan(uv.y, uv.x);
    float f = 0.5 + 0.5 * cos(6.0 * (a + time) + r * 10.0);
    col += vec3(f * 0.3, f * 0.2, f * 0.5);

    // Tähdet
    for(int i = 0; i < 10; i++) { // Lisää tähtiä
        vec2 starPos = vec2(fract(sin(float(i) * 43758.5453) * 0.1 + time), fract(sin(float(i) * 23421.6313) * 0.1 + time));
        starPos = starPos * 2.0 - 1.0;
        starPos.x *= iResolution.x / iResolution.y;
        vec2 d = uv - starPos;
        float star = 1.0 / length(d) * 0.05; // Pienennä tähtien kokoa
        col += vec3(star);
    }

    // Välkkyvät tähdet
    float flicker = fract(sin(dot(uv.xy + time * 20.0, vec2(12.9898,78.233))) * 43758.5453); // Lisää nopeutta
    col += 0.1 * vec3(flicker, flicker * 0.5, flicker * 0.2);

    // Plasmaefekti
    float plasma = sin(uv.x * 10.0 + time * 2.0) * cos(uv.y * 10.0 + time * 2.0); // Lisää nopeutta
    col += vec3(0.2, 0.1, 0.3) * plasma;

    // Morffautuva sumu
    float morph = sin(time + r * 10.0) * 0.5 + 0.5;
    col *= vec3(morph, morph * 0.8, morph * 1.2);

    // Lopullinen väri
    fragColor = vec4(col, 1.0);
}
