#define NUM_SEGS 1000
#define NUM_SEGS1 30
#define NUM_SEGS2 50

float sdLine( in vec2 p, in vec2 a, in vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ); }

float Line(vec2 uv, vec2 a, vec2 b) {
	vec2 uva = uv - a, ba = b - a;
    float t = clamp(dot(uva, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a + ba * t;
    float d = length(c - uv);
	return min(1.0, 0.0015/d); }

float ellips(vec2 center, float a, float b, vec2 coord) {
    float x = coord.x - center.x,
          y = coord.y - center.y,
          d = 1.0;
    d = pow(x/a, 2.) + pow(y/b, 2.) - 1.0;
    return d; }

vec2 Bezier(vec2 A, vec2 B, vec2 C, vec2 D, float t) {
    return mix(mix(mix(A, D, t), mix(D, C, t), t), mix(mix(D, C, t), mix(C, B, t), t), t); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;

    uv *= 1.0;

    vec3 col = vec3(0.0); 

    float d = 1e3,
          f = 1e3,
          t = 1e3;

    vec2 A = vec2(0.105, 0.065),
         B = vec2(0.105, -0.065),
         C = vec2(0.24, 0.045),
         D = vec2(0.15, 0.1),
         A1 = vec2(0.1, 0.095),
         B1 = vec2(0.1, -0.095),
         C1 = vec2(0.15, -0.08),
         D1 = vec2(0.15, 0.08),
         A2 = vec2(0.1, 0.095),
         B2 = vec2(0.1, -0.095),
         C2 = vec2(0.055, -0.08),
         D2 = vec2(0.055, 0.08),
         A3 = vec2(-0.105, 0.065),
         B3 = vec2(-0.105, -0.065),
         C3 = vec2(-0.24, 0.045),
         D3 = vec2(-0.15, 0.1),
         A4 = vec2(-0.1, 0.095),
         B4 = vec2(-0.1, -0.095),
         C4 = vec2(-0.15, -0.08),
         D4 = vec2(-0.15, 0.08),
         A5 = vec2(-0.1, 0.095),
         B5 = vec2(-0.1, -0.095),
         C5 = vec2(-0.055, -0.08),
         D5 = vec2(-0.055, 0.08);

    col = mix(col, vec3(1.0, 0.6, 0.1), smoothstep(-0.02, 0.02, -ellips(vec2(0.0, 0.0), 0.04, 0.100, uv + vec2(-.100, 0.0))));
    col = mix(col, vec3(0.0, 0.0, 0.0), smoothstep(-0.02, 0.02, -ellips(vec2(0.0, 0.0), 0.03, 0.085, uv + vec2(-.105, 0.0))));
    d = min(d, min(min(
        sdLine(uv, vec2(0.095, 0.03), vec2(0.105, 0.063)),
        sdLine(uv, vec2(0.095, 0.03), vec2(0.112, -0.02))),
        sdLine(uv, vec2(0.112, -0.02), vec2(0.105, -0.065))));
    col = mix(col, vec3(0.2, 0.2, 1.0), smoothstep(-0.02, 0.02, -ellips(vec2(0.0, 0.0), 0.04, 0.100, uv + vec2(0.100, 0.0))));
    col = mix(col, vec3(0.0, 0.0, 0.0), smoothstep(-0.02, 0.02, -ellips(vec2(0.0, 0.0), 0.03, 0.085, uv + vec2(0.105, 0.0))));
    f = min(f, min(min(
        sdLine(uv, vec2(-0.115, 0.03), vec2(-0.105, 0.063)),
        sdLine(uv, vec2(-0.115, 0.03), vec2(-0.098, -0.02))),
        sdLine(uv, vec2(-0.098, -.02), vec2(-0.105, -.065))));
    col += min(1.0, 0.0015/f) * vec3(1.0, 0.3, 0.1);
    col += min(1.0, 0.0012/d) * vec3(1.0, .05, 0.3);

    vec2 P, PP=A;
    for (int i = 1; i<=NUM_SEGS; i++) {
        t = float(i)/float(NUM_SEGS);
        P = Bezier(A, B, C, D, t);
        col += vec3(0.8,0.1,0.5) * 0.03 * smoothstep(0.0, 1.0, Line(uv, P, PP));
        PP = P; }

    vec2 P1, PP1=A1;
    for (int i = 1; i<=NUM_SEGS1; i++) {
        t = float(i)/float(NUM_SEGS1);
        P1 = Bezier(A1, B1, C1, D1, t);
        col += vec3(1.0, 0.6, 0.1) * 0.15 * Line(uv, P1, PP1);
        PP1 = P1; }

    vec2 P2, PP2=A2;
    for (int i = 1; i<=NUM_SEGS2; i++) {
        t = float(i)/float(NUM_SEGS2);
        P2 = Bezier(A2, B2, C2, D2, t);
        col += vec3(1.0, 0.6, 0.1) * 0.1 * Line(uv, P2, PP2);
        PP2 = P2; }

    vec2 P3, PP3=A3;
    for (int i = 1; i<=NUM_SEGS; i++) {
        t = float(i)/float(NUM_SEGS);
        P3 = Bezier(A3, B3, C3, D3, t);
        col += vec3(1.0, 0.3, 0.1) * 0.04 * smoothstep(0.0, 1.0, Line(uv, P3, PP3));
        PP3 = P3; } 

    vec2 P4, PP4=A4;
    for (int i = 1; i<=NUM_SEGS1; i++) {
        t = float(i)/float(NUM_SEGS1);
        P4 = Bezier(A4, B4, C4, D4, t);
        col += vec3(0., 0., 1.0) * 0.2 * Line(uv, P4, PP4);
        PP4 = P4; }

    vec2 P5, PP5=A5;
    for (int i = 1; i<=NUM_SEGS2; i++) {
        t = float(i)/float(NUM_SEGS2);
        P5 = Bezier(A5, B5, C5, D5, t);
        col += vec3(0., 0., 1.0) * 0.15 * Line(uv, P5, PP5);
        PP5 = P5; }

    fragColor = vec4(col, 1.0); }
