float modIntegers(float f){
    // return the number f modulo the integers in the range (-0.5,0.5)
    f += 0.5;
    return f-floor(f)-0.5;
}

float smoothModInteger(float f){
    return 1.-smoothstep(0.,0.08,abs(modIntegers(f)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.2*(fragCoord-iResolution.xy/2.)/iResolution.y;

    // Mobius matrix
    mat2 m = mat2(
        0 , 1,
        -1, iTime
        );

    mat2 mm = inverse(m);

    // apply matrix to vector
    vec2 preNum   = mm[0][0]*uv+mm[0][1]*vec2(1.,0.);
    vec2 preDenon = mm[1][0]*uv+mm[1][1]*vec2(1.,0.);

    // take the ratio
    vec2 numerator = vec2(
        preNum.x*preDenon.x+preNum.y*preDenon.y,
        preNum.y*preDenon.x-preNum.x*preDenon.y);
    float denominator = length(preDenon);
    denominator *= denominator;

    vec2 z = numerator/denominator;


    // Output to screen
    vec3 color = vec3(
        smoothModInteger(z.x),
        smoothModInteger(z.y),
        smoothModInteger(length(z))
        );
    fragColor = vec4(color,1.0);
}
