float getGlow(float dist, float radius, float intensity)
{
    return pow(radius/dist, intensity);
}

float sdBox(in vec2 p, in vec2 b)
{
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    float whRatio = iResolution.x / iResolution.y;
    vec2 pos = (uv * 2.0f - 1.0f);
    pos.x *= whRatio;

    float dist = sdBox(pos, vec2(0.5f, 0.5f));
    float glow = getGlow(abs(dist), 0.015f, 1.55f);

    vec3 col = 10.0 * vec3(smoothstep(0.01f, 0.003f, abs(dist)));
    col += glow * vec3(1.0f, 0.1f, 0.2f);
    col = 1.0f - exp(-col);
    col = pow(col, vec3(0.8f));

    float alpha = pow(1.0f - exp(-glow), 0.8f);

    col += vec3(1.0f, 1.0f, 1.0f) * smoothstep(0.0f, 1.0f, 1.0f - alpha) * smoothstep(0.0f, 1.0f, 1.0f - glow) * step(dist, 0.0f);

    fragColor = vec4(col, 1.0f);
}
