float pi = 3.1415926535897932384626433832795;
vec3 objec(vec3 u)
{
    vec2 m = 2.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    if(iMouse.z<.5){m = vec2(pi*.5);}
    vec3 n = vec3(atan(u.y,u.x)+m.x,
                 atan(u.z,length(u.xy))+m.y,
                 log(length(u)));
        n = vec3(cos(n.y)*cos(n.x),
                 cos(n.y)*sin(n.x),
                 sin(n.y))*exp(n.z);
    for(float i = .5; i < 4.; ++i)
    {
        u = vec3(atan(u.y,u.x),
                 atan(u.z,length(u.xy)),
                 log(length(u)));    u = u*-4.;
        u = vec3(cos(u.y)*cos(u.x),
                 cos(u.y)*sin(u.x),
                 sin(u.y))*exp(u.z);   u = u+n;
    }
    float l = dot(u,u);
    vec3 b = vec3(atan(u.y,u.x),
                  atan(u.z,length(u.xy)),
                  log(length(u)));
         b = cos(4.*b+2.)*.5+.5;
    vec3 c = vec3(0);
    float a = 0.;  if(l==l && l<9.){a=max(0.,1.5-l); c = b;}
    return max(1.-a*c,0.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camDir = normalize(cos(iTime*vec3(.27,.71,.51)+vec3(0,1,3)));
    vec3 camPos = -camDir*1.5;
	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec3 sd = 1.5*normalize(vec3(camDir.z,0,-camDir.x));
    vec3 up = 1.5*normalize(cross(camDir,sd));
    camPos += sd*uv.x + up*uv.y;

    vec3 ray = camDir;

    vec3 p = camPos;
    vec3 c = vec3(0);                      //reflected light
    vec3 g = vec3(1.3);                      //directed light
    for (int i = 0; i < 333; ++i)          //ray march
    {
        vec3 r = objec(p);
        vec3 g2 = g;
        g *= r;
        c += g*r*(g2-g);
        p += ray*3./333.;
    }
	fragColor = vec4(c,0);
}
