#define PI 3.1415926535

float circle(vec2 p, float r) {
	return length(p) - r;
}

float wireCircle(vec2 p, float r) {
	float d1 = circle(p, r);

	float d2 = circle(p, r - (r * 0.1f));

	return max(d1, -d2);
}

mat2 rotate(float rad) {
	float s = sin(rad);

	float c = cos(rad);

	return mat2(c, -s,
				s, c);
}

vec2 Union(vec2 a, vec2 b) {
	return a.x < b.x ? a : b;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uvi = fragCoord.xy;

	vec2 uv = (uvi * 2.0f - iResolution.xy) / iResolution.y;

	vec3 col = vec3(0.0f);

	float r = 0.5f;

	float r2 = 0.05f;

	vec2 result = vec2(0.0f);

	vec2 result2 = vec2(0.0f);

	vec2 tempUV = uv;

	for (float i = 0.0f; i < 3.0f; i++) {
		vec2 pos = vec2(0.0f);

		if (i == 1.0f) {
			tempUV *= rotate(PI * 0.3f);
		}

		if (i == 2.0f) {
			tempUV *= rotate(PI * 0.4f);
		}

		pos.x += r;

		pos *= rotate(iTime * 3.0f + i * 2.0f);

		pos.x /= 3.0f;

		float d = circle(tempUV + pos, r2);

		if (i == 0.0f) {
			result = vec2(d, i);
		}

		else {
			result = Union(result, vec2(d, i));
		}
	}

	uv *= rotate(PI * 0.3f);

	uv.x *= 3.0f;

	float d = wireCircle(uv, r);

	result2 = vec2(d, 0.0f);

	uv.x /= 3.0f;

	uv *= rotate(PI * -0.3f);

	uv *= rotate(PI * 0.7f);

	uv.x *= 3.0f;

	d = wireCircle(uv, r);

	result2 = Union(result2, vec2(d, 1.0f));

	uv.x /= 3.0f;

	uv *= rotate(PI * -0.7f);

	uv.x *= 3.0f;

	d = wireCircle(uv, r);

	result2 = Union(result2, vec2(d, 2.0f));

	uv.x /= 3.0f;

	vec3 tempCol = vec3(0.0f);

	if (result.x < 0.0f) {
        float index = mod(result.y - 1.0f, 3.0f);

		if (index == 0.0f) {
            tempCol = vec3(1.0f, 0.0f, 0.0f);
        }

		else if (index == 1.0f) {
            tempCol = vec3(0.0f, 1.0f, 0.0f);
        }

		else if (index == 2.0f) {
            tempCol = vec3(0.0f, 0.0f, 1.0f);
        }
	}

	else if (result2.x < 0.0f) {
        float index = result2.y;

		if (index == 0.0f) {
            tempCol = vec3(1.0f, 0.0f, 0.0f);
        }

		else if (index == 1.0f) {
            tempCol = vec3(0.0f, 1.0f, 0.0f);
        }

		else if (index == 2.0f) {
            tempCol = vec3(0.0f, 0.0f, 1.0f);
        }
	}

	col += tempCol;

	fragColor = vec4(col, 1.0f);
}
