vec2 rotate_45(vec2 v) {
    return vec2((v.x - v.y), (v.x + v.y)) / sqrt(2.0);
}

vec2 rotate_45_with_pivot(vec2 v, vec2 pivot) {
    return rotate_45(v - pivot) + pivot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 converted_res = vec2(iResolution.x * 6.0, iResolution.y);

    vec2 texture_size = iChannelResolution[0].xy;
    vec2 texels_per_pixel = texture_size / converted_res;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = rotate_45_with_pivot(fragCoord, vec2(750.0, 422.0)) / converted_res;

    //uv += texels_per_pixel * vec2(cos(iTime)/6.0, sin(iTime));    
    vec2 pixel = uv * texture_size;


    // Nearest neighbor
    pixel = floor(pixel) + 0.5;


    // Inigo Quilez
    /*
    vec2 seam = floor(pixel + 0.5);
    vec2 dudv = fwidth(pixel);
    pixel = seam + clamp( (pixel - seam) / dudv, -0.5, 0.5);
    /*

    /*
    // Casey Muratori
    vec2 fat_pixel = floor(pixel) + 0.5;
    fat_pixel += 1.0 - clamp((1.0 - fract(pixel)) * texels_per_pixel, 0.0, 1.0);
    pixel = fat_pixel;
    */

    uv = pixel / texture_size;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}
