void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float angle = atan(uv.y, uv.x);
    float radius = length(uv);

    float spirals1 = 6.0;
    float speed1 = iTime * 0.5;
    float pattern1 = sin(radius * 10.0 - spirals1 * angle + speed1);

    float spirals2 = 10.0;
    float speed2 = -iTime * 0.8;
    float pattern2 = cos(radius * 8.0 - spirals2 * angle + speed2);

    float circles = cos(iTime * 1.0 + radius * 25.0) * 0.5 + 0.5;

    float combinedPattern = mix(pattern1, pattern2, 0.6 + 0.4 * sin(iTime * 0.5));
    combinedPattern = combinedPattern * circles;

    vec3 darkPurple1 = vec3(0.3, 0.0, 0.6);
    vec3 darkPurple2 = vec3(0.15, 0.0, 0.2);
    vec3 brightPurple = vec3(0.3, 0.2, 0.6);

    vec3 color = mix(darkPurple1, darkPurple2, smoothstep(0.0, 1.0, combinedPattern));
    color = mix(color, brightPurple, 0.3 + 0.2 * sin(iTime * 0.5));

    fragColor = vec4(color, 1.0);
}
