#define R iResolution
#define AR R.x/R.y
#define t iTime/1.8
#define gzoom 5.
#define zoom 3.5 
#define beat(a) .3+max(sin(3.*a),-cos(6.*a-1.5)-1.) //from: https://www.shadertoy.com/view/wltyRB

float bckgr(vec2 uv){
    uv /= zoom;
    return min(step(0.01*AR,fract((uv.x*gzoom))),step(0.01*AR,fract((uv.y*gzoom))));
}

float curve(vec2 p){
    vec2 o = p;
    float g = p.x-t;
    //https://www.desmos.com/calculator/2bqvtdd6vd
    p.y = 0.4-(sin(g*4.)+sin(g*8.)/6.)*2.5*(-(floor(sin(g*2.))+0.1))*(1.-floor(mod(sin(g/1.5),2.)));
    float d = distance(o,vec2(p.x,p.y));
    return  smoothstep(fwidth(d),0.,d-.04);
}


float heart(vec2 p){
    vec2 o = p;
    float x = p.x;
    float y = p.y;
    y = pow(x*x+y*y-1./beat(t*2.8),3.)/(x*x*y*y);//inimioara
    p= vec2(x,y);
    float d = distance(o,p);
    return smoothstep( (p.y-o.y),0., d );
}

void mainImage( out vec4 o, in vec2 i )
{
    i = (2.*i-R.xy ) / R.y ;
    i *=zoom;
    float d = mix(heart(i),curve(i),-.4);
    vec3 col = mix(vec3(.3),vec3(.7),bckgr(i)); 
    col = mix(col,vec3(1,0,0),d);
    o = vec4(col,1.);
}
