float h21( vec2 p ) {
    return fract(372.54*sin(dot(vec2(125.265, 454.32), p) + 652.45));
}

float noise (in vec2 st) {
    st.y = st.x+st.y;
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = h21(i);
    float b = h21(i + vec2(1.0, 0.0));
    float c = h21(i + vec2(0.0, 1.0));
    float d = h21(i + vec2(1.0, 1.0));

    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.*uv - 1.;
    uv.x *= iResolution.x/iResolution.y;
    float t = iTime;

    /////////////////////////

    uv.x += t*.7;
    uv *= 8.;

    vec2 iuv = floor(uv);
    vec2 fuv = fract(uv);


    float s = step(
        .65,
        noise(.7*(iuv + ((fuv.x > fuv.y) ? .6 : 0.)) )
    );

    vec3 col = mix(
        vec3(0.15),
        vec3(1.),
        s
    );

    fragColor = vec4(col,1.0);
}
