float WCircle (vec2 uv,vec2 p,float r,float blur){
    float d = length(uv-p);     
    float c = smoothstep(r,r-blur,d);
    return c;
}

float BCircle (vec2 uv,vec2 p,float r,float blur){
    float d = length(uv-p);     
    float c = smoothstep(r,r+blur,d);
    return c;
}

vec3 smile (vec2 uv, vec2 p, float size, vec3 col){
    uv-=p;
    uv/=size;
    float mask = WCircle(uv,vec2(0.,0.),0.35,0.01);
    mask -= WCircle(uv,vec2(0.1,0.1),0.05,0.01);
    mask -= WCircle(uv,vec2(-0.1,0.1),0.05,0.01);    
    float mouth = WCircle(uv,vec2(0.,0.),0.2,0.01);    
    mouth -= WCircle(uv,vec2(0.,0.1),0.2,0.01);    
    if(mouth > 1.) mouth =1.;
    if(mouth < 0.) mouth =0.;
    mask -= mouth;
    vec3 col2 = col*mask;

    return col2;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 mask = smile(uv,vec2(0.4,0.),1.,vec3(0.,1.,0));
    mask += smile(uv,vec2(-0.4,0.),1.,vec3(1.,0.,0));
    mask += smile(uv,vec2(0.,0.35),0.3,vec3(0.,0.,1.));
    mask += smile(uv,vec2(0.,-0.35),0.3,vec3(1.,1.,0));
    mask += smile(uv,vec2(-0.75,-0.35),0.3,vec3(1.,0.,1.));
    mask += smile(uv,vec2(0.75,-0.35),0.3,vec3(0.,1.,1));
    mask += smile(uv,vec2(-0.75,0.35),0.3,vec3(1.,1.,1));
    mask += smile(uv,vec2(0.75,0.35),0.3,vec3(1,0.4,0.));







    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(mask,1.0);
}
