float PI = 3.1415;



bool isWithinBounds(float toCheck, float targetR, float tolerance) {
    return toCheck < targetR + tolerance/2. && toCheck > targetR - tolerance/2.;
}

float atan2(float x, float y) {
    if(x <0.) return y < 0.? atan(y/x) + PI: atan(y/x) - PI;
    else return atan(y/x);
}

vec4 wave1Cont(vec2 uv, float speed, float arcs, float size, float amp, float tolerance, vec4 fillCol) {
    vec4 toReturn = vec4(0,0,0,0);
    if(isWithinBounds(uv.x, amp*sin(arcs*uv.y + iTime*speed)+size, tolerance)) toReturn += fillCol;
    return toReturn;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float minres = min(iResolution.x, iResolution.y)/2.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvc = (fragCoord - .5*iResolution.xy) / minres;
    // Normalized r theta coords, x = r, y = theta
    vec2 uv = vec2(sqrt(uvc.x*uvc.x + uvc.y*uvc.y), mod(atan2(uvc.y,uvc.x),2.*3.14));

    vec4 bgCol = vec4(1,0,1,1);
    vec4 col = vec4(0.,0.4 + uvc.y/3.,0.6 + uvc.y/10.,1);

    col += wave1Cont(uv, 1., 5., 0.7, 0.06, 0.2+sin(iTime)*0.05, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 5., 4., 0.7, 0.05+0.01*sin(iTime*10.), 0.2, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 10., 8., 0.7+0.05*sin(iTime*2.), 0.01, 0.2, vec4(0.25,0.25,0.25,0));

    col += wave1Cont(uv, 4., 10., 1.25, 0.03, 0.1+sin(iTime)*0.01, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 3., 3., 1.25, 0.05+0.01*sin(iTime*10.), 0.1, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 1., 6., 1.25+0.05*sin(iTime*2.), 0.01, 0.1, vec4(0.25,0.25,0.25,0));

    col += wave1Cont(uv, 1., 5., 1.8, 0.06, 0.2+sin(iTime)*0.05, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 2., 7., 1.8, 0.05+0.01*sin(iTime*10.), 0.2, vec4(0.25,0.25,0.25,0));
    col += wave1Cont(uv, 5., 20., 1.8+0.05*sin(iTime*2.), 0.01, 0.2, vec4(0.25,0.25,0.25,0));
    fragColor = col;
}
