#define PI 3.14
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.354;
    vec3 color = vec3(0.0);
    float intensity = 10.0;

    int num_particles = max(min(300-int((texture(iChannel0, vec2(0)).r*1000.0)),300), 20);
    float gglow = max(((texture(iChannel0, vec2(0)).g*80.0)),30.0)- (sin(time+7.0)+1.0) * 30.0 +40.0;
    //gglow = 
    for (int i = 0; i < num_particles; i++) {
        float id = float(i) / float(num_particles) +texture(iChannel1, uv).r;

        float particleTime = id;

        float theta = fract(id + particleTime * 0.1) * 6.28318;
        float phi = fract(id * 1.618 + particleTime * 2.65) * 3.14159;

        vec3 spherePos = vec3(
            sin(phi) * cos(theta),
            sin(phi) * sin(theta),
            cos(phi)/1.01
        );

        float angle = time * 0.8;
        mat3 rotation = mat3(
            cos(angle), 0.0, -sin(angle),
            0.0, 1.0, 0.0,
            sin(angle), 0.0, cos(angle)
        );
        spherePos = rotation * spherePos;

        vec2 screenPos = spherePos.xy / (spherePos.z + 1.5);
        float dist = length(uv - screenPos);
        float glow = exp(-dist * gglow);

        intensity += glow * 0.004;
        color += glow * vec3(
            0.4 + 0.6 * sin(id * 7.28318), 
            0.3 + 0.4 * sin(time * 2.0), 
            1.0
        );
    }

    fragColor = vec4(color, 1.0) * intensity;
}

