void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
    p= mod(p,vec2(1,0.1));
    p = p + vec2(1,0.65); 
    float d = length(p-vec2(0.5,0.5));
    float phi = atan(p.y/p.x)+iTime*.8;
    float phi2 =phi+(fragCoord.xy / iResolution.xy).y;
    p = vec2(p.x+.5*sin(phi2),p.y); 

    vec3 col = vec3(texture(iChannel0,p).r,texture(iChannel0, p).g,texture(iChannel0, p).b);

    float theta = atan(col.y/col.x)+iTime*.8;
    col = col+mod(0.5*sin(theta),0.1);
    fragColor = vec4(col, 1.0);
}
