float cc(float r, float b, vec2 uv, vec2 p){
    float d= length(uv-p);
    return smoothstep(r, r-b,d);
}

float smiley(vec2 uv,vec2 p, float s){
   float blur = .01;
   uv -= p;
   uv *=s;


   float mask= cc(.3,blur,uv,vec2(.5,.5));
   mask-= cc(.06,blur,uv,vec2(.4,.6));

   mask-= cc(.06,blur,uv,vec2(.6,.6));

   float mouth= cc(.2,blur,uv,vec2(.5,.5));
   float mouth2 = cc(.2,blur,uv,vec2(.5,.55));

   mouth -= mouth2;

   float face = mask - mouth;
   return face;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
   vec2 uv = fragCoord/iResolution.xy;
   uv.x *= iResolution.x/iResolution.y;


   vec3 col = vec3(0.);


   //float face = smiley(uv, vec2(0),2.4);
   //float face2 = smiley(uv, vec2(0.5),2.4);
   float faces = 0.0;

   for(int i=0; i< 30; i++){

      float n = float(i) * 0.2;
      float scale = cos(1. * iTime + uv.x ) * 4.;
      float face = smiley(uv, vec2(0.+ n,0.5),scale);
      faces +=face ;

   }


   col = vec3(1. * cos(iTime+uv.y),0. ,1. ) *  faces;


    // Output to screen
   fragColor = vec4(col,1.0);
}
