void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Определение центра изображения
    vec2 center = iResolution.xy / 2.0;

    // Определение количества секторов
    int numSectors = 100;

    // Определение угла для каждого сектора
    float angleStep = 2.0 * 3.14159 / float(numSectors);

    // Определение текущего угла пикселя относительно центра
    vec2 dir = fragCoord - center;
    float angle = atan(dir.y, dir.x);

    // Определение индекса текущего сектора
    int sectorIndex = int((angle + 3.14159) / angleStep);

    // Генерация случайного индекса для смены секторов
    float randomValue = fract(sin(float(sectorIndex) * 12.9898) * 43758.5453);
    int swapSectorIndex = int(randomValue * float(numSectors));

    // Перемещение цвета пикселя в другой сектор
    if (sectorIndex == swapSectorIndex) {
        fragColor = texture(iChannel0, fragCoord / iResolution.xy); // Оставляем цвет без изменений
    } else {
        // Новый угол для пикселя
        float newAngle = float(swapSectorIndex) * angleStep - 3.14159;
        vec2 newPos = center + vec2(cos(newAngle), sin(newAngle)) * length(dir);
        fragColor = texture(iChannel0, newPos / iResolution.xy);
    }
}
