// Playing with noise, made something like ripples but I like them best in green as a sort of neon light

// ---------------------------------
// Simplex Noise functions by iq https://www.shadertoy.com/view/Msf3WH

vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

// -----------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // obtain position and uv
    vec2 pos = (fragCoord/iResolution.xy);
    vec2 uv1 = pos*vec2(iResolution.x/iResolution.y,1.0) + vec2(iTime*0.02,-(iTime)*0.012);
    vec2 uv2 = pos*vec2(iResolution.x/iResolution.y,1.0) + vec2(iTime*0.01,(iTime)*0.016);

    // get noise values
    float modifier = 0.6;
    float val = modifier*noise(uv1*4.)+modifier*noise(uv2*6.);

    // manipulate noise
    float absVal = abs(val);
    float white = 1.-smoothstep(0.,0.1,absVal);
    float green = 1.-smoothstep(0.,0.6,absVal);

    // Output to screen
    fragColor = vec4(vec3(white, white+0.5*green, white),1.0);
}
