void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 guv = (uv*10.0)-10.0;
    float x = guv.x;
    float y = guv.y;


    float ys = sin(y);
    float y2s = sin(2.0*y);
    float yc = cos(y);
    float ycc = yc*yc;
    float yss = ys*ys;

    float xs = sin(x);
    float x2s = sin(2.0*x);
    float xc = cos(x);
    float xcc = xc*xc;
    float xss = xs*xs;

    float result = ((y2s/xcc)-xc)*((x2s/ycc)-yc);

    // Output to screen
    float blue = result;
    float red = -result;
    float green=0.0;
    if (result>-1.0&&result<1.0) {green=1.0;}

    fragColor = vec4(0, green ,0,1.0);
}
