vec3 palette (float t) {
    vec3 a = vec3(1.138, 0.638, 0.522);
    vec3 b = vec3(0.718, 0.369, 0.977);
    vec3 c = vec3(0.683, 0.383, 0.317);
    vec3 d = vec3(3.117, 3.024, 0.887);

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    float mvt = cos(iTime * 1.25) * 5.0 / 7.5;

    // Fractal pattern
    for(float i=0.0; i < 4.0; i++) {

        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) + exp(-length(uv0));

        vec3 col = palette(length(uv0) + mvt * 2.05);

        d = sin(d * 5.0 + mvt) / 5.5;
        d = abs(d);
        d = 0.015 / d;
        d = pow(d, 1.2);

        finalColor += col * d / 1.15;
    }


    fragColor = vec4(finalColor, 1.0);
}
