// from https://www.shadertoy.com/view/lcdXRM

#define x2 x*x
#define x3 x*x*x
#define x4 x*x*x*x
#define x5 x*x*x*x*x
#define x6 x*x*x*x*x*x
#define x7 x*x*x*x*x*x*x

#define y2 y*y
#define y3 y*y*y
#define y4 y*y*y*y
#define y5 y*y*y*y*y
#define y6 y*y*y*y*y*y
#define y7 y*y*y*y*y*y*y

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 guv = (uv*20.0)-10.0;
    float x = guv.x;
    float y = guv.y;

    float result = (2.0 * x4 * y3)-(x4 * y2)-(x2 * y4)+10.0;

    // Output to screen
    float blue = abs(min(result, 0.0))/1000.0;
    float red = max(result, 0.0)/100.0;
    float white = 1.0/(abs(result)+0.001);

    fragColor = vec4(red+white, white, blue+white,1.0);
}
