vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

float CrateCheckerBoard(vec2 uv,float cellSize)//创建棋盘格https://www.shadertoy.com/view/lXdSzX
{
    vec2 checkeruv = step(mod(uv , cellSize * 2.), vec2(cellSize));
    return mix(checkeruv.x,1.-checkeruv.x,checkeruv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );//uv的居中与反拉伸

    float mask = CrateCheckerBoard(uv,.2);//创建棋盘格

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = mix(col,1.-col,mask);

    fragColor = vec4(col,1.0);
}
