void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u_resolution = iResolution.xy;
    vec2 u_mouse = iMouse.xy;
    float u_time = iTime;

    vec3 colorA = vec3(0.8, 0.01, 0.01);
    vec3 colorB = vec3(1.0, 0.0, 0.0);

    vec2 st = fragCoord.xy / u_resolution;

    float pct = 0.0;
    pct = 2.0 * length(st - vec2(0.5, 0.8));

    if (pct < 0.5) {
        float theta = asin(pct * 2.0);
        float newLen = sin(theta) * 0.1;
        pct *= newLen;
    }

    pct = ((5.0 * smoothstep(0.5, 0., (0.9 - pct))) + smoothstep(0.6, 0.1, (pct + 0.236)));

    vec3 color = mix(colorB, colorA, pct - 0.4);

    fragColor = vec4(color, 1.0);
}
