#define L length((u+u-R)*e*.75 + .1*vec2(t-5., 7)

void mainImage( out vec4 o, vec2 u )
{
    vec2 R = iResolution.xy;

    float t,
          e = 1./R.y, 
          i;

    for (o *= i; 
         i++ < 10.; 
         o += smoothstep(-e, e, 
             .04 - min(L + sin(1.57*(cos(.314*t)-vec2(0,1)))), 
                       L))))   
        t = mod(iTime - i, 10.);         
}
