const float scale = 2.;
const float speed = 5.;
const int maxI = 128;
const float threshold = 0.5;
const float add = 1.;
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = scale * (2.*I - iResolution.xy)/iResolution.y;
    float val = iTime * speed;
    for(int i; i < maxI; i++)
    {
        if(mod(val, uv.x) < uv.x * threshold)
        {
            val /= uv.x;
        }
        else
        {
            val = val*uv.y + add;
        }
    }
    O = vec4(clamp( abs(mod(val*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 ),1.);
}
