#define MAX_STEPS 100
#define MAX_STEP_DISTANCE 100.
#define MINIMUM_SURFACE_DISTANCE .001

#define CAMERA_RADIUS 5.
#define CAMERA_ANGLE_MODIFIER 7.

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

struct Shape {
    vec3 pos;
    float dist;
};

#define CASTLE_WALLS_OFFSET 1.5

void offsetAndRepeatTower(out Shape target) {
    target.pos.xz = abs(target.pos.xz);
    target.pos.xz -= CASTLE_WALLS_OFFSET;
}

void constructTower(out Shape towerBase, out Shape towerTop, out Shape towerIntersection, in vec3 p) {
    float yOffset = .6;
    towerTop.pos.y -= yOffset;
    towerIntersection.pos.y -= yOffset;

    offsetAndRepeatTower(towerTop);
    offsetAndRepeatTower(towerIntersection);
    offsetAndRepeatTower(towerBase);

    // Top
    float topScale = mix(1., 2., smoothstep(1., -.25, p.y));
    towerTop.pos.xz *= topScale;

    // Top - Boolean
    towerTop.dist = sdBox(towerTop.pos, vec3(.45,.15,.45)) / topScale;
    towerIntersection.dist = sdBox(towerIntersection.pos, vec3(.3,.45,.3));

    // Base
    float baseScale = mix(1., 2., smoothstep(-.25, 1., towerBase.pos.y));
    towerBase.pos.xz *= baseScale;

    towerBase.dist = sdBox(towerBase.pos, vec3(.5)) / baseScale;   
}

void offsetAndRepeatWallX(out Shape target) {
    target.pos.x = abs(target.pos.x);
    target.pos.x -= CASTLE_WALLS_OFFSET;
}

void offsetAndRepeatWallZ(out Shape target) {
    target.pos.z = abs(target.pos.z);
    target.pos.z -= CASTLE_WALLS_OFFSET;
}

void constructWallX(out Shape wall, in vec3 p) {
    offsetAndRepeatWallX(wall);

    float scaleMultiplier = CASTLE_WALLS_OFFSET;

    wall.pos *= vec3(
        1.,
        1.,
        1. / scaleMultiplier    
    );

    wall.dist = sdBox(wall.pos, vec3(.05,.4,.8)) / scaleMultiplier;
}

void constructWallZ(out Shape wall, in vec3 p) {
    offsetAndRepeatWallZ(wall);

        float scaleMultiplier = CASTLE_WALLS_OFFSET;

    wall.pos *= vec3(
        1. / scaleMultiplier,
        1.,
        1.
    );

    wall.dist = sdBox(wall.pos, vec3(.8,.4,.05));
}

float getSDFSceneDistance(vec3 p) {
    Shape towerBase;
    towerBase.pos = p - vec3(0, .5, 0);

    Shape towerTop;
    towerTop.pos = towerBase.pos;

    Shape towerIntersection; 
    towerIntersection.pos = p - vec3(0,1,0);

    constructTower(
        towerBase,
        towerTop,
        towerIntersection,
        p
    );

    float towerUnionDist = max(towerTop.dist, -towerIntersection.dist); 
    float towerDist = min(towerUnionDist, towerBase.dist);

    Shape wallX;
    wallX.pos = p - vec3(0,.4,0);;

    Shape wallZ;
    wallZ.pos = p - vec3(0,.4,0);

    constructWallX(wallX, p);
    constructWallZ(wallZ, p);

    float wallDist = min(wallX.dist, wallZ.dist);

    float planeDist = p.y;

    float dist = min(wallDist, planeDist);
    dist = min(dist, towerDist);

    return dist;
}

float rayMarch(vec3 rayOrigin, vec3 rayDirection) {
	float distanceFromOrigin = 0.0;

    for(int i=0; i < MAX_STEPS; i++) {
    	vec3 p = rayOrigin + rayDirection * distanceFromOrigin;
        float dist = getSDFSceneDistance(p);
        distanceFromOrigin += dist;

        if(distanceFromOrigin > MAX_STEP_DISTANCE || dist < MINIMUM_SURFACE_DISTANCE) {
            break;
        }
    }

    return distanceFromOrigin;
}

vec3 getNormal(vec3 p) {
	float d = getSDFSceneDistance(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        getSDFSceneDistance(p-e.xyy),
        getSDFSceneDistance(p-e.yxy),
        getSDFSceneDistance(p-e.yyx));

    return normalize(n);
}

float diffuseLight(vec3 p) {
    vec3 lightPosition = vec3(0, 3, 3);

    vec3 lightVector = normalize(lightPosition-p);
    vec3 normal = getNormal(p);

    float diffuse = clamp(dot(normal, lightVector), 0., 1.);
    float distanceFromOrigin = rayMarch(p + normal * MINIMUM_SURFACE_DISTANCE * 2., lightVector);

    if(distanceFromOrigin < length(lightPosition - p)) diffuse *= .5;

    return diffuse;
}


mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);

    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 color = vec3(0);

    vec3 camera = vec3( 
        CAMERA_RADIUS * cos(CAMERA_ANGLE_MODIFIER * mouse.x), 
        3.0, 
        CAMERA_RADIUS * sin(CAMERA_ANGLE_MODIFIER * mouse.x)
    );

    vec3 lookAt = vec3(0, 1, 0);

    mat4 view = viewMatrix(camera, lookAt, vec3(0, 1, 0));

    vec3 viewDirection = normalize(vec3(st, -1.0));
    viewDirection =  (view * vec4(viewDirection, 0.0)).xyz;

    vec3 rayOrigin = camera;
    vec3 rayDirection = viewDirection;

    float distanceFromOrigin = rayMarch(camera, viewDirection);

    vec3 p = rayOrigin + rayDirection * distanceFromOrigin;

    float diffuse = diffuseLight(p);
    color = vec3(diffuse);

    color = pow(color, vec3(.4545));	// gamma correction

    fragColor = vec4(color,1.0);
}
