void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalization
    vec2 uv = fragCoord / iResolution.xy;

    // Background Color
    vec3 backgroundColor = vec3(0.01, 0.01, 0.1);

    // Speed Controller
    float t = iTime * 0.5;

    // Stars Framework
    vec3 starColor = vec3(1.0);
    float starAmount = 100.0; 
    vec2 starUV = floor(uv * starAmount) + vec2(t * 0.02, t * 0.01); 

    // Use of Interpolation
    float interpolationFactor = 0.5 * (1.0 + sin(iTime));
    vec2 interpolationStarUV = mix(starUV, vec2(t * 0.01, t * 0.005), interpolationFactor);

    // Star twinkle effect 
    float starRandomValue = fract(sin(dot(interpolationStarUV, vec2(12.9898, 78.233)) + t) * 43758.5453);     

    // Star size
    float star = smoothstep(0.02, 0.04, starRandomValue); 

    // Final Color 
    fragColor = vec4(mix(starColor, backgroundColor, star), 1.0);
}
