#ifdef GL_ES
precision mediump float;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float u_time = iTime;
vec4 u_mouse = iMouse;
vec3 u_resolution = iResolution;

  float w = max(u_resolution.x, u_resolution.y);
  vec2 p = fragCoord.xy / w;
    vec2 m = u_mouse.xy / u_resolution.xy;
    vec2 from_mouse = m - p;
    float dist_from_mouse = length(from_mouse);

    float ring = dist_from_mouse * 50. + 15. * sin(u_time);
    float n = floor(ring);

    float r = (sin(u_time + n) + 1.5) / 2.5;
    float g = (sin(2. * (u_time + n)) + 1.5) / 2.5;
    float b = (sin(3. * (u_time + n)) + 1.5) / 2.5;
    vec3 color = step(.7, fract(ring)) * vec3(r, g, b);

    fragColor = vec4(color,1.0);
}
