vec4 fragColor = vec4(0,0,0,0);
float radius = 1.0;
float ratio = 100.0;

vec2 apply_transform(vec2 point)
{
    return vec2((iResolution.x/2.0 - point.x)/ratio, (iResolution.y/2.0 - point.y)/ratio);
}

bool isInDisk(vec2 point, float radius, vec2 center)
{
    return pow(point.x+center.x,2.0) + pow(point.y+center.y,2.0) - pow(radius,2.0) < 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pt = apply_transform(fragCoord.xy);

    vec2 center = vec2(0,0);

    if(isInDisk(pt, radius, center))
        fragColor = vec4(1,0,0,1);
    else
        fragColor = vec4(0,0,0,1);
}
