void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.5;

    // Перемещение кардиоиды
    vec2 movement = vec2(sin(iTime * 0.5), cos(iTime * 0.75)) * 0.5;
    uv -= movement;

    // Параметры кардиоиды
    float a = 0.5;
    float theta = atan(uv.y, uv.x);

    float r = a * (1.0 - 0.4 * cos(theta * 6.0 + t)) + 0.1 * sin(theta * 8.0 + t * 1.5)
              + 0.05 * sin(theta * 12.0 + t * 3.0);


    float dist = length(uv) - r;
    vec3 color = vec3(0.0, 0.0, 1.0);
    float edge = smoothstep(0.01, 0.03, dist);

    // Градиенты
    vec3 gradientColor = vec3(0.5 + 0.5 * cos(theta + t), 0.0, 1.0);
    color = mix(color, gradientColor, edge);


    //Пульсация радиуса
    float pulse = 0.5 + 0.5 * sin(t * 3.0);
    r = mix(r, pulse, 0.5);
    dist = length(uv) - r;

    // Шум
    float noise = (sin(uv.x * 15.0 + t) + sin(uv.y * 15.0 + t)) * 0.1;
    color += vec3(0.1, 0.1, 0.2) * noise;

    float transparency = 0.6;
    vec4 finalColor = vec4(color, transparency);
    float lightIntensity = max(0.0, dot(normalize(vec3(0.0, 1.0, 1.0)), normalize(vec3(uv, 1.0))));
    finalColor.rgb += lightIntensity * vec3(0.6, 0.6, 0.8);
    float glow = 0.05 / (dist * dist + 0.1);
    finalColor.rgb += glow * vec3(0.3, 0.4, 0.6);

    fragColor = finalColor;
}
