void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * uv;

    vec3 rayOrigin = vec3(0.0, 0.0, -5.0);
    vec3 rayDirection = normalize(vec3(uv, 1.5));
    float distance = 1.0;
    vec3 color = vec3(0.0);

    for (int i = 0; i < 100; i++) 
    {
        vec3 rayPosition = rayOrigin + distance * rayDirection;
        float terrainHeight = sin(rayPosition.x * 6.0) + cos(rayPosition.y * 200.0);
        float sceneDist = rayPosition.y - terrainHeight;

        vec3 interpolatedColor = mix(vec3(0.2, 0.7, 0.9), vec3(1.0, 0.8, 0.2), smoothstep(0.0, 0.1, abs(sceneDist)));

        interpolatedColor *= vec3(1.0, 0.8, 0.2) + 0.2 * cos(vec3(2.0, 3.0, 1.0) * iTime + uv.xyx);

        color += interpolatedColor * 0.9;

        distance += 0.2;

        if (abs(sceneDist) < 0.5 || distance > 20.0) 
        {
            break;
        }
    }

    color *= 0.5 + 0.5 * sin(iTime);

    fragColor = vec4(color, 1.0);
}
