// Raymarched Fire

#define TIME iTime 

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0);

    // Camera position
    vec3 ro = vec3(0, 0, 4);

    // Ray direction
    vec3 rd = normalize(vec3(uv, -1)); 

    float d = 0.0; // Distance

    // Ray march
    for(int i = 0; i < 64; i++) {
       vec3 p = ro + rd * d;

       // Noise to disturb flame
       float noise = smoothstep(0.3, 0.7, sin(p.z*5.0 + TIME*3.0));

       // Add flame colors based on noise
       if(noise > 0.5) {
           color += vec3(1.0, 0.5, 0.1) * (1.0 - noise);  
       }

       // Increment distance
       d += 0.05;

       // Stop if reached flame depth
       if(d > 4.0) break; 
    }

    fragColor = vec4(color, 1.0);
}
