#define tau 6.28318530
#define n1 0.2
#define n2 0.2
#define n3 0.2

// Function definitions
    float f(float x, float y, float z) {
        return x*x-y*z+sin(167.3)+n1*sin(1.*iTime);
    }

    float g(float x, float y, float z) {
        return z*z-x*y+sin(1.3*167.3)+n2*sin(1.4*iTime);
    }
    float h(float x, float y, float z) {
        return y*y-x*z+sin(1.6*167.3)+n3*sin(1.6*iTime);
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = 1.7*fragCoord / iResolution.xy-0.85;

    // Iterations
    int n = 10;

    // Initial values
    float x = uv.x, y = uv.y, z=(uv.x-uv.y)/2.;

    for(int i = 0; i < n; ++i) {
        float newX = f(x, y, z);
        float newY = g(x, y, z);
        float newZ = h(x ,y ,z);
        x = newX;
        y = newY;
        z = newZ;
    }

    vec3 col = vec3(abs(x), abs(y), abs(z));

    fragColor = vec4(col, 1.0);
}
