const float diffuse_seconds = 7.0;
const float cutoff_radii = 1.1;
const float wave_length = 0.53 / 2.0;
const float wave_amplitude = 1.25; 
const float wave_frequency = 1.0;

float sdf_circle(vec2 p, float r) {
    return length(p) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
        float time = iTime * 0.1;
    float size = time;
    float sdf = sdf_circle(uv, time);    
    float value = pow(mod(sdf * (1.0 / wave_length), 1.0), 2.0);
    float cutoff = 1.0 - smoothstep(0.0, cutoff_radii, length(uv));

    vec3 pink = vec3(1.0, 0.5, 1.0);
    vec3 violet = vec3(0.7, 0.5, 1.0);
    vec3 baseBlue = vec3(0.1, 0.5, 1.0);
    vec3 lightBlue = vec3(0.1, 0.8, 1.0);

    vec3 colorMix1 = mix(baseBlue, pink, sqrt(value));
    vec3 colorMix2 = mix(lightBlue, violet, sqrt(value));
    fragColor = vec4(mix(colorMix1, colorMix2, cutoff), 1.0);
}
