void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Gradient red frame with rounded corners
    float radius = 0.22;  // Adjust this value to control the corner roundness
    vec2 center = vec2(0.5);
    vec2 distanceVec = abs(uv - center) - vec2(radius);

    float squareDistance = length(max(distanceVec, 0.0));

    vec3 frameColor = vec3(0.0, 0.0, 0.0) * smoothstep(radius, radius + 0.06, squareDistance);

    // Output the final color with alpha 1.0
    fragColor = vec4(frameColor, 1.0);
}
